    mutationFn: async (data: InsertBjjBooking) => {
      const response = await apiRequest("POST", "/api/bjj-booking", data);
      return response.json();
    },
    onSuccess: () => {
      setIsSubmitted(true);
      toast({
        title: "Booking Request Submitted!",
